// Default images
export const DEFAULT_IMAGES = {
  PROFILE: 'data:image/svg+xml;base64,',
  MEETING: 'data:image/svg+xml;base64,',
  GYM: 'data:image/svg+xml;base64,',
  CLUB: 'data:image/svg+xml;base64,',
} as const;

// Get default image by type
export function getDefaultImage(type: 'profile' | 'meeting' | 'gym' | 'club'): string {
  switch (type) {
    case 'profile':
      return DEFAULT_IMAGES.PROFILE;
    case 'meeting':
      return DEFAULT_IMAGES.MEETING;
    case 'gym':
      return DEFAULT_IMAGES.GYM;
    case 'club':
      return DEFAULT_IMAGES.CLUB;
    default:
      return DEFAULT_IMAGES.PROFILE;
  }
}
